/* XPOM-KZ Custom Styles */

:root {
    --primary-color: #4e73df;
    --primary-dark: #3e5bcc;
    --success-color: #1cc88a;
    --warning-color: #f6c23e;
    --danger-color: #e74a3b;
    --info-color: #36b9cc;
    --purple-color: #6f42c1;
    --light-color: #f8f9fc;
    --dark-color: #2c3e50;
    --gray-100: #f8f9fa;
    --gray-200: #e3e6f0;
    --gray-300: #dddfeb;
    --gray-400: #d1d3e2;
    --gray-500: #b7b9cc;
    --gray-600: #858796;
    --gray-700: #6e707e;
    --gray-800: #5a5c69;
    --gray-900: #3a3b45;
    --shadow: 0 0.15rem 1.75rem 0 rgba(58, 59, 69, 0.15);
    --shadow-lg: 0 1rem 3rem rgba(0, 0, 0, 0.175);
}

/* Base Styles */
body {
    font-family: 'Nunito', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
    background-color: var(--light-color);
    color: var(--gray-800);
    line-height: 1.6;
}

/* Navigation */
.navbar-brand {
    font-weight: 700;
    font-size: 1.2rem;
}

.brand-name {
    font-weight: 800;
    font-size: 1.3rem;
    color: var(--primary-color);
}

.brand-subtitle {
    font-size: 0.75rem;
    color: var(--gray-600);
    font-weight: 400;
}

.brand-icon {
    width: 45px;
    height: 45px;
    background: linear-gradient(135deg, #4e73df, #6f42c1);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.3rem;
}

.nav-text-status {
    display: flex;
    align-items: center;
    color: #1cc88a;
    font-size: 0.9rem;
    font-weight: 600;
    padding: 8px 15px;
    background: rgba(28, 200, 138, 0.1);
    border-radius: 20px;
}

.btn-navbar {
    background: linear-gradient(135deg, #4e73df 0%, #224abe 100%);
    border: none;
    color: white;
    font-weight: 600;
    padding: 10px 25px;
    border-radius: 25px;
    transition: all 0.3s ease;
    text-decoration: none;
    font-size: 0.9rem;
}

.btn-navbar:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(78, 115, 223, 0.3);
    color: white;
    text-decoration: none;
}

/* Hero Section */
.hero-section {
    padding: 100px 0 60px 0;
    background: linear-gradient(135deg, #f8f9fc 0%, #e3e6f0 50%, #f8f9fc 100%);
    color: #2c3e50;
    position: relative;
    overflow: hidden;
}

.hero-title {
    font-size: 3.5rem;
    font-weight: 800;
    margin-bottom: 1.5rem;
    line-height: 1.1;
    background: linear-gradient(135deg, #4e73df 0%, #6f42c1 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-title .text-dark {
    background: #2c3e50;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-subtitle {
    font-size: 1.1rem;
    color: #6e707e;
    font-weight: 400;
    margin-bottom: 2rem;
    line-height: 1.6;
}

/* Service Cards */
.services-section {
    padding: 80px 0;
    background: white;
}

.service-card {
    background: white;
    border-radius: 20px;
    padding: 40px 30px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    border: 0;
    transition: all 0.3s ease;
    text-align: center;
    height: 100%;
    border-top: 4px solid transparent;
}

.service-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 40px rgba(0, 0, 0, 0.12);
}

.service-icon {
    width: 80px;
    height: 80px;
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 25px;
    font-size: 2rem;
    color: white;
}

.service-icon-blue {
    background: linear-gradient(135deg, #4e73df 0%, #224abe 100%);
}

.service-icon-purple {
    background: linear-gradient(135deg, #6f42c1 0%, #5a359c 100%);
}

.service-icon-green {
    background: linear-gradient(135deg, #1cc88a 0%, #17a673 100%);
}

.service-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: #2c3e50;
    margin-bottom: 15px;
}

.service-description {
    color: #6e707e;
    font-size: 1rem;
    line-height: 1.6;
    margin-bottom: 0;
}

/* Track Section */
.track-section {
    padding: 80px 0;
    background: #f8f9fc;
}

.track-card {
    background: white;
    border-radius: 20px;
    padding: 50px 40px;
    box-shadow: 0 4px 25px rgba(0, 0, 0, 0.08);
    border: 0;
    text-align: center;
}

.track-title {
    font-size: 2rem;
    font-weight: 700;
    color: #2c3e50;
    margin-bottom: 30px;
}

.track-label {
    font-size: 1rem;
    font-weight: 600;
    color: #5a5c69;
    margin-bottom: 8px;
    display: block;
}

.track-input {
    border: 2px solid #e3e6f0;
    border-radius: 12px;
