}

.track-input:focus {
    border-color: #4e73df;
    box-shadow: 0 0 0 0.2rem rgba(78, 115, 223, 0.25);
    background: white;
}

.btn-track {
    background: linear-gradient(135deg, #4e73df 0%, #224abe 100%);
    border: none;
    border-radius: 12px;
    padding: 18px 30px;
    font-size: 1.1rem;
    font-weight: 600;
    color: white;
    transition: all 0.3s ease;
    margin-top: 15px;
}

.btn-track:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(78, 115, 223, 0.3);
    color: white;
}

/* Main Services */
.main-services {
    padding: 80px 0;
    background: #f8f9fc;
}

.section-title {
    font-size: 2.5rem;
    font-weight: 800;
    color: #2c3e50;
    margin-bottom: 1rem;
}

.section-subtitle {
    font-size: 1.1rem;
    color: #6e707e;
    margin-bottom: 3rem;
}

.main-service-card {
    background: white;
    border-radius: 20px;
    padding: 40px 35px;
    box-shadow: 0 4px 25px rgba(0, 0, 0, 0.08);
    border: 0;
    height: 100%;
    transition: all 0.3s ease;
}

.main-service-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 40px rgba(0, 0, 0, 0.12);
}

.service-header {
    display: flex;
    align-items: center;
    margin-bottom: 30px;
}

.service-icon-main {
    width: 70px;
    height: 70px;
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
    color: white;
    font-size: 1.8rem;
    flex-shrink: 0;
}

.service-header-text h3 {
    font-size: 1.4rem;
    font-weight: 700;
    color: #2c3e50;
    margin-bottom: 5px;
}

.service-header-text p {
    font-size: 0.95rem;
    color: #6e707e;
    margin-bottom: 0;
}

.service-list {
    list-style: none;
    padding: 0;
    margin-bottom: 30px;
}

.service-list li {
    padding: 8px 0;
    font-size: 0.95rem;
    color: #5a5c69;
    display: flex;
    align-items: center;
}

.service-list li i {
    margin-right: 12px;
    color: #1cc88a;
    font-size: 0.9rem;
}

.btn-service {
    border: none;
    border-radius: 12px;
    padding: 16px 30px;
    font-size: 1rem;
    font-weight: 600;
    color: white;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
}

.btn-service:hover {
    transform: translateY(-2px);
    color: white;
    text-decoration: none;
}

.btn-service-green {
    background: linear-gradient(135deg, #1cc88a 0%, #17a673 100%);
}

.btn-service-green:hover {
    box-shadow: 0 8px 25px rgba(28, 200, 138, 0.3);
}

.btn-service-blue {
    background: linear-gradient(135deg, #4e73df 0%, #224abe 100%);
}

.btn-service-blue:hover {
    box-shadow: 0 8px 25px rgba(78, 115, 223, 0.3);
}

/* Contact Section */
.contact-section {
    padding: 80px 0;
    background: white;
}

.contact-card {
    background: white;
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 4px 25px rgba(0, 0, 0, 0.08);
    border: 0;
    height: 100%;
}

.advantages-card {
    background: #f8f9fc;
    border-radius: 20px;
    padding: 35px 30px;
    box-shadow: 0 4px 25px rgba(0, 0, 0, 0.08);
    border: 0;
    height: 100%;
}

.contact-methods .contact-method {
    display: flex;
    align-items: center;
    margin-bottom: 1.5rem;
    padding: 15px;
    background: #f8f9fc;
    border-radius: 12px;
}

.contact-icon {
    width: 50px;
    height: 50px;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    margin-right: 1rem;
    font-size: 1.1rem;
    flex-shrink: 0;
}

.contact-icon.phone {
    background: linear-gradient(135deg, #4e73df 0%, #224abe 100%);
}

.contact-icon.email {
    background: linear-gradient(135deg, #1cc88a 0%, #17a673 100%);
}

.advantage-item {
    display: flex;
    align-items: flex-start;
    margin-bottom: 1.2rem;
    padding: 12px 0;
}

.advantage-item i {
    margin-right: 0.75rem;
    margin-top: 0.25rem;
    font-size: 1.1rem;
}

.advantage-item strong {
    color: #2c3e50;
    font-size: 1rem;
}

.advantage-item small {
    color: #6e707e;
    font-size: 0.9rem;
}

/* Footer */
.footer-modern {
    background: #2c3e50;
    color: #ecf0f1;
    padding: 60px 0 30px 0;
    margin-top: 80px;
}

.footer-brand {
    font-size: 1.5rem;
    font-weight: 800;
    color: white;
    margin-bottom: 10px;
    display: block;
}

.footer-description {
    color: #bdc3c7;
    font-size: 0.95rem;
    line-height: 1.5;
}

.footer-contact {
    color: #ecf0f1;
}

.footer-heading {
    color: #ecf0f1;
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 8px;
}

.footer-phone {
    color: #3498db;
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 0;
}

.footer-email {
    color: #2ecc71;
    font-size: 1rem;
    margin-bottom: 0;
}

.footer-bottom {
    border-top: 1px solid #34495e;
    margin-top: 40px;
    padding-top: 25px;
    text-align: center;
}

.footer-copyright {
    color: #95a5a6;
    font-size: 0.9rem;
    margin-bottom: 0;
}

/* Buttons */
.btn-gradient {
    background: linear-gradient(135deg, var(--primary-color), var(--purple-color));
    border: none;
    color: white;
    font-weight: 600;
    padding: 0.75rem 2rem;
    border-radius: 12px;
    transition: all 0.3s ease;
}

.btn-gradient:hover {
    background: linear-gradient(135deg, var(--primary-dark), #5a32a3);
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(78, 115, 223, 0.3);
}

.btn-lg {
    padding: 1rem 2.5rem;
    font-size: 1.1rem;
}

/* Forms */
.form-section {
    margin-bottom: 2.5rem;
    padding-bottom: 2rem;
    border-bottom: 1px solid var(--gray-200);
}

.form-section:last-child {
    border-bottom: none;
    margin-bottom: 0;
}

.section-title {
    font-size: 1.3rem;
    font-weight: 700;
    color: var(--gray-800);
    margin-bottom: 1.5rem;
    display: flex;
    align-items: center;
}

.section-title i {
    margin-right: 0.75rem;
    font-size: 1.1rem;
}

.form-label.required::after {
    content: " *";
    color: var(--danger-color);
}

.form-control {
    border: 1px solid var(--gray-300);
    border-radius: 8px;
    padding: 0.75rem 1rem;
    transition: all 0.3s ease;
}

.form-control:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 0.2rem rgba(78, 115, 223, 0.25);
}

/* Order Status */
.success-icon {
    width: 120px;
    height: 120px;
    margin: 0 auto 2rem;
    background: linear-gradient(135deg, var(--success-color), #17a085);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 4rem;
}

.status-timeline {
    position: relative;
}

.timeline {
    position: relative;
    padding-left: 3rem;
}

.timeline::before {
    content: '';
    position: absolute;
    left: 1.5rem;
    top: 0;
    bottom: 0;
    width: 2px;
    background: var(--gray-300);
}

.timeline-item {
    position: relative;
    margin-bottom: 2rem;
    padding-left: 1.5rem;
}

.timeline-marker {
    position: absolute;
    left: -2.25rem;
    top: 0.25rem;
    width: 2.5rem;
    height: 2.5rem;
    background: var(--gray-300);
    border-radius: 50%;
