    padding-bottom: 1.5rem;
    border-bottom: 1px solid var(--gray-200);
}

.info-section:last-child {
    border-bottom: none;
}

.info-section .section-title {
    font-size: 1.1rem;
    margin-bottom: 1rem;
}

.info-list {
    margin-bottom: 0;
}

.info-list dt {
    font-weight: 600;
    color: var(--gray-700);
    font-size: 0.9rem;
}

.info-list dd {
    margin-bottom: 0.5rem;
    color: var(--gray-800);
}

.route-info {
    position: relative;
}

.route-point {
    display: flex;
    align-items: flex-start;
    margin-bottom: 1rem;
}

.route-icon {
    margin-right: 1rem;
    margin-top: 0.25rem;
    font-size: 1.2rem;
}

.route-details {
    flex: 1;
}

.route-arrow {
    text-align: center;
    margin: 0.5rem 0;
}

.info-card {
    background: var(--gray-100);
    border-radius: 12px;
    padding: 1.5rem;
    margin-bottom: 1rem;
}

.info-card h6 {
    font-weight: 700;
    color: var(--gray-800);
    margin-bottom: 1rem;
}

.info-card ul {
    margin-bottom: 0;
}

.info-card li {
    margin-bottom: 0.5rem;
    font-size: 0.9rem;
}

/* Authentication */
.auth-card {
    background: white;
    border-radius: 20px;
    padding: 50px 40px;
    box-shadow: var(--shadow-lg);
    border: 0;
}

.auth-icon {
    width: 80px;
    height: 80px;
    background: linear-gradient(135deg, var(--primary-color), var(--purple-color));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1.5rem;
    color: white;
    font-size: 2rem;
}

.benefits-section {
    margin-top: 2rem;
}

.benefits-section h6 {
    color: var(--gray-800);
    font-weight: 700;
    margin-bottom: 1rem;
}

.benefits-section li {
    margin-bottom: 0.75rem;
    font-size: 0.9rem;
    color: var(--gray-700);
}

.demo-credentials {
    background: var(--light-color);
    border: 1px solid var(--gray-300);
    border-radius: 12px;
}

.demo-credentials h6 {
    color: var(--gray-800);
    font-weight: 700;
}

/* Profile */
.profile-avatar {
