    height: 100px;
    background: linear-gradient(135deg, var(--primary-color), var(--purple-color));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1rem;
    color: white;
    font-size: 3rem;
}

.profile-info dt {
    font-weight: 600;
    color: var(--gray-700);
    font-size: 0.9rem;
}

.profile-info dd {
    margin-bottom: 0.75rem;
    color: var(--gray-800);
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;
}

.stat-item {
    text-align: center;
    padding: 1rem;
    background: var(--light-color);
    border-radius: 12px;
}

.stat-number {
    font-size: 1.8rem;
    font-weight: 800;
    color: var(--primary-color);
    margin-bottom: 0.25rem;
}

.stat-label {
    font-size: 0.8rem;
    color: var(--gray-600);
    font-weight: 600;
}

.admin-actions {
    border-top: 1px solid var(--gray-200);
}

.empty-state {
