}

.bg-gradient-success {
    background: linear-gradient(135deg, var(--success-color), #17a085);
}

.bg-gradient-info {
    background: linear-gradient(135deg, var(--info-color), #2e8b9e);
}

.bg-gradient-warning {
    background: linear-gradient(135deg, var(--warning-color), #e6a800);
}

.route-preview {
    font-size: 0.8rem;
}

.route-preview .route-point {
    margin-bottom: 0.25rem;
    display: flex;
    align-items: center;
}

.route-preview .route-point i {
    margin-right: 0.5rem;
    font-size: 0.7rem;
}

.route-preview .route-arrow {
    text-align: center;
    margin: 0.1rem 0;
}

.route-preview .route-arrow i {
    font-size: 0.6rem;
}

.route-section {
    padding: 1.5rem;
    background: var(--light-color);
    border-radius: 12px;
    margin-bottom: 1rem;
}

.route-section h6 {
    margin-bottom: 1rem;
    font-weight: 700;
}

.system-info .info-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid var(--gray-200);
}

.system-info .info-item:last-child {
    border-bottom: none;
    margin-bottom: 0;
}

.report-card {
    height: 100%;
    transition: all 0.3s ease;
}

.report-card:hover {
    transform: translateY(-3px);
    box-shadow: var(--shadow);
}

.metric-card {
    padding: 1rem;
    background: var(--light-color);
    border-radius: 12px;
    margin-bottom: 1rem;
}

.metric-card h4 {
    font-weight: 800;
    margin-bottom: 0.25rem;
}

.chart-container {
    position: relative;
    height: 300px;
}

.chart-area {
    position: relative;
    height: 320px;
}

.chart-pie {
    position: relative;
    height: 250px;
}

.map-container {
    background: linear-gradient(135deg, #e3f2fd 0%, #f3e5f5 100%);
    border: 2px dashed var(--gray-300);
}

.map-marker {
    cursor: pointer;
    transition: all 0.3s ease;
}

.map-marker:hover {
    transform: scale(1.2);
}

.route-analysis .route-item {
    transition: all 0.3s ease;
}

.route-analysis .route-item:hover {
    background: var(--light-color);
    transform: translateX(5px);
}

/* Footer */
footer {
    background: var(--dark-color);
    color: white;
}

footer .brand-name {
    color: white;
}

footer .contact-info {
    font-size: 0.9rem;
}

footer .advantage-item {
    margin-bottom: 0.75rem;
    font-size: 0.85rem;
}

footer .advantage-item i {
    margin-right: 0.5rem;
}

/* Responsive */
@media (max-width: 768px) {
    .hero-title {
        font-size: 2.5rem;
    }

    .hero-subtitle {
        font-size: 1.1rem;
    }

    .service-card,
    .main-service-card,
    .track-card-main,
    .auth-card {
        padding: 30px 20px;
    }

    .stats-grid {
        grid-template-columns: 1fr;
    }

    .section-title {
        font-size: 2rem;
    }
}

@media (max-width: 576px) {
    .hero-section {
        padding: 60px 0;
    }

    .hero-title {
        font-size: 2rem;
    }

    .services-section,
    .main-services,
    .contact-section,
    .track-section {
        padding: 40px 0;
    }

    .service-card {
        margin-bottom: 1.5rem;
    }
}

/* Utility Classes */
.shadow {
    box-shadow: var(--shadow) !important;
}

.shadow-lg {
    box-shadow: var(--shadow-lg) !important;
}

.border-start-primary {
    border-left: 4px solid var(--primary-color) !important;
}

.border-start-success {
    border-left: 4px solid var(--success-color) !important;
}

.border-start-warning {
    border-left: 4px solid var(--warning-color) !important;
}

.border-start-danger {
    border-left: 4px solid var(--danger-color) !important;
}

.text-xs {
    font-size: 0.75rem;
}

.font-weight-bold {
    font-weight: 700 !important;
}

/* Animation */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in-up {
    animation: fadeInUp 0.6s ease forwards;
}

/* Loading States */
.loading {
    opacity: 0.6;
    pointer-events: none;
}

.spinner-border-sm {
    width: 1rem;
    height: 1rem;
}

/* Print Styles */
@media print {
    .navbar,
    .btn,
    .admin-main .card-header .dropdown,
    footer {
        display: none !important;
    }

    .card {
        border: 1px solid #000 !important;
        box-shadow: none !important;
    }

    .admin-main {
        margin: 0 !important;
        padding: 0 !important;
    }
}

